def happy_sets(n, k):
    MOD = 998244353

    def factorial(x):
        if x == 0:
            return 1
        else:
            return (x * factorial(x - 1)) % MOD

    def inverse(x):
        return pow(x, MOD - 2, MOD)

    dp = [[0] * (k + 1) for _ in range(n + 1)]

    for i in range(1, n + 1):
        for j in range(min(i, k) + 1):
            for p in range(j + 1):
                dp[i][j] += (dp[i - 1][j - p] * factorial(p)) % MOD * inverse(factorial(p + 1))
                dp[i][j] %= MOD

    ans = 0
    for i in range(k + 1):
        ans += dp[n][i]
        ans %= MOD

    return ans


