def solve(n, l, a):
    a.sort()
    max_distance = 0
    for i in range(1, n):
        max_distance = max(max_distance, a[i] - a[i-1])
    if a[0] == 0 and a[-1] == l:
        return (l / (2 * n)) ** 0.5
    elif a[0] == 0:
        return (a[-1] / n) ** 0.5
    elif a[-1] == l:
        return (l - a[0]) / n ** 0.5
    else:
        return min((max(a[-1], l - a[0]) / n) ** 0.5, (max_distance / 2) ** 0.5)

def main():
    t = 1
    for _ in range(t):
        n, l, a = read_input()
        print(f"{solve(n, l, a):.10f}")

