import sys
from math import gcd
from collections import defaultdict

def main():
    n = int(sys.stdin.readline())
    A = sys.stdin.readline().strip()
    B = sys.stdin.readline().strip()

    m = len(A)
    k = len(B)

    def compute_gcd(a, b):
        return gcd(a, b)

    def compute_lcm(a, b):
        return a * b // compute_gcd(a, b)

    lcm = compute_lcm(m, k)

    # Build the frequency dictionary for the first lcm rounds
    freq = defaultdict(int)
    for i in range(lcm):
        a = A[i % m]
        b = B[i % k]
        freq[(a, b)] += 1

    q = n // lcm
    r = n % lcm

    # Now process the remainder r rounds
    temp_freq = defaultdict(int)
    for i in range(r):
        a = A[i % m]
        b = B[i % k]
        temp_freq[(a, b)] += 1

    # Merge temp_freq into freq
    for (a, b), cnt in temp_freq.items():
        freq[(a, b)] += cnt

    # Determine the counts
    a_count = 0
    b_count = 0
    beats = {('R', 'S'), ('S', 'P'), ('P', 'R')}

    for (a, b), cnt in freq.items():
        if a == b:
            continue
        if (a, b) in beats:
            b_count += cnt
        else:
            a_count += cnt

    print(a_count, b_count)

if __name__ == "__main__":
    main()

