def is_snowflake(n):
    if n < 3:
        return False
    # Try all possible k starting from 2 up to sqrt(n)
    max_k = int(n**0.5) + 1
    for k in range(2, max_k + 1):
        # We need to find m such that n = (k^(m+1) - 1)/(k-1)
        # Rearranged: k^(m+1) = (n*(k-1) + 1)
        # Let's compute target = n * (k - 1) + 1
        target = n * (k - 1) + 1
        # Now check if target is a perfect power of k
        current = k
        exponent = 1
        while current < target:
            next_current = current * k
            if next_current == target:
                return True
            current = next_current
            exponent += 1
            if current > target:
                break
        # Also check if k=2 and m=1 is possible (but it's handled above)
    return False

