    for m in range(2, 61):
        target = n
        low = 2
        high = target
        while low <= high:
            mid = (low + high) // 2
            try:
                power = mid ** (m + 1)
            except OverflowError:
                high = mid - 1
                continue
            numerator = power - 1
            denominator = mid - 1
            if denominator == 0:
