def is_power_of_two(n):
    """Check if a number is a power of two."""
    return n > 0 and (n & (n - 1)) == 0

def can_exist(k, n):
    """Check if a snowflake with k vertices can exist."""
    # Initialize the count of vertices and the depth.
    count = 1
    depth = 0
    while True:
        # If the current vertex is connected to only one other vertex, we need to add k new vertices.
        if count == 2 ** depth:
            if count + k > n:
                return False
            count += k
        # If the current vertex is connected to more than one other vertex, we don't need to add any new vertices.
        else:
            count += 1
        # If we have reached the target number of vertices, return True.
        if count == n:
            return True
        # Increment the depth.
        depth += 1

