from collections import defaultdict, deque

def main():
    input = sys.stdin.read().split()
    idx = 0
    n = int(input[idx])
    m = int(input[idx+1])
    idx += 2

    edges = []
    for _ in range(m):
        a = int(input[idx])-1  # convert to 0-based
        b = int(input[idx+1])-1
        c = int(input[idx+2])
        edges.append((a, b, c))
        idx +=3

    # Build adjacency list
    adj = [[] for _ in range(n)]
    for a, b, c in edges:
        adj[a].append( (b, c) )
        adj[b].append( (a, c) )

    # Compute in-degree for Kahn's algorithm
    in_degree = [0] * n
    for a, b, c in edges:
        in_degree[b] += 1
        in_degree[a] += 1

    # Kahn's algorithm for topological sort
    q = deque()
    for i in range(n):
        if in_degree[i] == 0:
            q.append(i)

    topo_order = []
    while q:
        u = q.popleft()
        topo_order.append(u)
        for v, c in adj[u]:
            in_degree[v] -= 1
            if in_degree[v] == 0:
                q.append(v)

    # Now, assign directions
    result = [0]*m
    reverse_edges = defaultdict(list)
    for a, b, c in edges:
        reverse_edges[a].append( (b, c) )
        reverse_edges[b].append( (a, c) )

    for i, (u, v, c) in enumerate(edges):
        if u in topo_order and v in topo_order:
            if u == topo_order[-1] or v == topo_order[0]:
                # Decide based on position in topo order
                pass
            else:
                # Assign direction to u -> v or v -> u
                # We need to have the sum of outflow equal inflow for each node
                # So, perhaps the direction is determined by the order in topo
                # If u comes before v in topo, then u must send flow to v
                # Else, v sends flow to u
                if u in topo_order:
                    if v in topo_order:
                        if topo_order.index(u) < topo_order.index(v):
                            result[i] = 0  # u -> v
                        else:
                            result[i] = 1  # v -> u
                else:
                    result[i] = 1  # since one is in topo, other is not, so v is in topo, so v->u
        else:
            # One of u or v is the source or sink
            if u == 0:  # source
                result[i] = 1  # since it can't have incoming edges, so v->u
            elif v == 0:
                result[i] = 0  # u->v
            else:
                # This case shouldn't happen as per problem constraints
                pass

    for res in result:
        print(res)

