import sys
from collections import deque

class Edge:
    def __init__(self, to, rev, capacity):
        self.to = to
        self.rev = rev
        self.capacity = capacity

class Dinic:
    def __init__(self, n):
        self.size = n
        self.graph = [[] for _ in range(n+1)]

    def add_edge(self, fr, to, cap):
        forward = Edge(to, len(self.graph[to]), cap)
        backward = Edge(fr, len(self.graph[fr]), 0)
        self.graph[fr].append(forward)
        self.graph[to].append(backward)

    def bfs_level(self, s, t, level):
        q = deque()
        level[:] = [-1] * (self.size + 1)
        level[s] = 0
        q.append(s)
        while q:
            u = q.popleft()
            for edge in self.graph[u]:
                if edge.capacity > 0 and level[edge.to] == -1:
                    level[edge.to] = level[u] + 1
                    q.append(edge.to)
                    if edge.to == t:
                        return

    def dfs_flow(self, u, t, flow, level, ptr):
        if u == t:
            return flow
        while ptr[u] < len(self.graph[u]):
            edge = self.graph[u][ptr[u]]
            if edge.capacity > 0 and level[u] < level[edge.to]:
                min_flow = min(flow, edge.capacity)
                result = self.dfs_flow(edge.to, t, min_flow, level, ptr)
                if result > 0:
                    edge.capacity -= result
                    self.graph[edge.to][edge.rev].capacity += result
                    return result
            ptr[u] += 1
        return 0

    def max_flow(self, s, t):
        flow = 0
        level = [-1] * (self.size + 1)
        while True:
            self.bfs_level(s, t, level)
            if level[t] == -1:
                return flow
            ptr = [0] * (self.size + 1)
            while True:
                f = self.dfs_flow(s, t, float('inf'), level, ptr)
                if f == 0:
                    break
                flow += f
            level = [-1] * (self.size + 1)

def main():
    input = sys.stdin.read().split()
    idx = 0
    n = int(input[idx])
    idx += 1
    m = int(input[idx])
    idx +=1
    edges = []
    for _ in range(m):
        ai = int(input[idx])
        idx +=1
        bi = int(input[idx])
        idx +=1
        ci = int(input[idx])
        idx +=1
        edges.append( (ai, bi, ci) )

    dinic_graph = Dinic(n)
    for ai, bi, ci in edges:
        dinic_graph.add_edge(ai, bi, ci)

    maxf = dinic_graph.max_flow(1, n)

    # Perform BFS on residual graph to find levels
    level = [-1] * (n + 1)
    q = deque()
    q.append(1)
    level[1] = 0
    while q:
        u = q.popleft()
        for edge in dinic_graph.graph[u]:
            if edge.capacity > 0 and level[edge.to] == -1:
                level[edge.to] = level[u] + 1
                q.append(edge.to)

    # Determine directions for each edge
    output = []
