        queue = deque()
        queue.append(s)
        vis[s] = True
        while queue:
            u = queue.popleft()
            for v, i, c in adj[u]:
                if not vis[v] and c > 0:
                    vis[v] = True
                    parent[v] = u
                    queue.append(v)
        return parent[t] != -1

