        else:
            group3.append(c)

    # Sort each group in descending order
    group1.sort(reverse=True)
    group2.sort(reverse=True)
    group3.sort(reverse=True)

    # Compute prefix sums for each group
    sum1 = [0]
    for c in group1:
        sum1.append(sum1[-1] + c)
    sum2 = [0]
    for c in group2:
        sum2.append(sum2[-1] + c)
    sum3 = [0]
    for c in group3:
        sum3.append(sum3[-1] + c)

    len1 = len(group1)
    len2 = len(group2)
    len3 = len(group3)

    max_possible = m
    dp = [-float('inf')] * (max_possible + 1)
    dp[0] = 0

    # Process group1
    for i in range(len1):
        c = group1[i]
        for w in range(max_possible, 0, -1):
            if w >= 1:
                if dp[w - 1] + c > dp[w]:
                    dp[w] = dp[w - 1] + c

    # Process group2
    for i in range(len2):
        c = group2[i]
        for w in range(max_possible, 0, -1):
            if w >= 2:
                if dp[w - 2] + c > dp[w]:
                    dp[w] = dp[w - 2] + c

    # Precompute max_dp array
    max_dp = [0] * (max_possible + 1)
    current_max = 0
    for w in range(max_possible + 1):
        if dp[w] > current_max:
            current_max = dp[w]
        max_dp[w] = current_max

    best = 0
    for k3 in range(0, len3 + 1):
        rem = m - 3 * k3
        if rem < 0:
            break
        if rem == 0:
            current_cost = sum3[k3]
        else:
            current_cost = sum3[k3] + max_dp[rem]
