import heapq

def main():
    import sys
    s = sys.stdin.read().strip()
    grid = [list(c) for c in s]
    rows = len(grid)
    cols = len(grid[0]) if rows > 0 else 0

    heap = []
    visited = {}

    initial_stack = [0]
    state = (0, 0, 'right', tuple(initial_stack))
    key = (0, 0, 'right', tuple(initial_stack))
    visited[key] = 0
    heapq.heappush(heap, (0, 0, 0, 'right', tuple(initial_stack)))

    found = False
    result = None

    while heap:
        initial_val, x, y, direction, stack = heapq.heappop(heap)

        if x >= rows or y >= cols:
            continue

        current_char = grid[x][y]

        new_stack = list(stack)
        new_direction = direction
        ip_change = False

        if current_char == '+':
            if len(new_stack) < 2:
                continue
            a = new_stack.pop()
            b = new_stack.pop()
            new_stack.append(a + b)
        elif current_char == '-':
            if len(new_stack) < 2:
                continue
            a = new_stack.pop()
            b = new_stack.pop()
            new_stack.append(a - b)
        elif current_char == '*':
            if len(new_stack) < 2:
                continue
            a = new_stack.pop()
            b = new_stack.pop()
            new_stack.append(a * b)
        elif current_char == '/':
            if len(new_stack) < 2:
                continue
            a = new_stack.pop()
            b = new_stack.pop()
            if b == 0:
                continue
            new_stack.append(a // b)
        elif current_char == '%':
            if len(new_stack) < 2:
                continue
            a = new_stack.pop()
            b = new_stack.pop()
            new_stack.append(a % b)
        elif current_char == '^':
            new_stack = new_stack[::-1]
        elif current_char == 'v':
            new_direction = 'down'
        elif current_char == '>':
            new_direction = 'right'
        elif current_char == '<':
            new_direction = 'left'
        elif current_char == ']':
            if not new_stack:
                found = True
                result = ''.join(map(str, new_stack))
                break
            else:
                continue
        else:
            if not new_stack:
                continue

        try:
            next_x, next_y = x, y
            if new_direction == 'right':
                next_x += 1
            elif new_direction == 'left':
                next_x -= 1
            elif new_direction == 'down':
                next_y += 1
            elif new_direction == 'up':
                next_y -= 1

            if next_x >= rows or next_y >= cols:
                if not new_stack:
                    continue
                else:
                    found = True
                    result = ''.join(map(str, new_stack))
                    break

            if (next_x, next_y, new_direction, tuple(new_stack)) not in visited:
                visited[(next_x, next_y, new_direction, tuple(new_stack))] = initial_val
                heapq.heappush(heap, (initial_val, next_x, next_y, new_direction, new_stack))
        except IndexError:
            continue

    if found:
        print(result)
    else:
        print("false")

if __name__ == "__main__":
    main()

