def main():
    import sys
    n = int(sys.stdin.readline())
    s = sys.stdin.readline().strip()

    from functools import lru_cache

    size = 1 << n
    max_mask = 1 << size

    @lru_cache(maxsize=None)
    def dp(start, end):
        m = (end - start + 1).bit_length() - 1
        phase = m - 1
        if start == end:
            return {start}
        mid = start + (end - start) // 2
        left = dp(start, mid)
        right = dp(mid+1, end)

        res = set()
        rule = s[phase]
        for a in left:
            for b in right:
                if rule == '0':
                    winner = min(a, b)
                else:
                    winner = max(a, b)
                res.add(winner)
        return res

    all_winners = dp(1, size)
    result = []
    for x in sorted(all_winners):
        result.append(str(x))
    print(' '.join(result))

