    for _ in range(2 * n - 2):
        s = sys.stdin.readline().strip()
        l = len(s)
        groups[l].append(s)

    # We'll try all possibilities using backtracking
    def backtrack(k, current_p, current_s):
        if k == n-1:
            return True, current_p, current_s
        next_k = k + 1
        if next_k not in groups:
            return False, None, None
        candidates = groups[next_k]
        for p, s in [(candidates[0], candidates[1]), (candidates[1], candidates[0])]:
            if p.startswith(current_p) and s.startswith(current_s):
                result, new_p, new_s = backtrack(next_k, p, s)
                if result:
                    return True, new_p, new_s
            if s.startswith(current_p) and p.startswith(current_s):
                result, new_p, new_s = backtrack(next_k, s, p)
                if result:
                    return True, new_p, new_s
        return False, None, None

    # Try both possibilities for k=1
    result, p_final, s_final = backtrack(1, None, None)
    # Now, assign the labels
    output = []
    for k in range(1, n):
        if p_final == groups[k][0]:
            output.append('P')
        else:
            output.append('S')
        if s_final == groups[k][1]:
            output.append('S')
        else:
            output.append('P')
