            end = (end // step) * step
        total_steps = ((end - start) // step) + 1
        if (a_prime == 1 or b_prime == 1):
            if a > b:
                min_k = max(0, (start + (a - b)) // a * a)
                min_k = max(min_k, 0)
                max_k = end
                if (max_k - min_k) % step == 0:
                    total = (max_k - min_k) // step + 1
                else:
                    total = (max_k - min_k) // step
                count = total
            else:
                min_k = 0
                max_k = end
                count = (max_k - min_k) // step + 1
        else:
            if a < b:
                min_k = max(0, (start + (b - a)) // b * b)
                min_k = max(min_k, 0)
