def frog_jumping(m, a, b):
    # Calculate the maximum reachable position
    max_reachable = min((m + b - 1) // a * a, m)

    # Initialize the prefix sum array
    prefix_sum = [0] * (max_reachable + 1)
    prefix_sum[0] = 1

    # Calculate the prefix sum array
    for i in range(1, max_reachable + 1):
        if i >= a:
            prefix_sum[i] += prefix_sum[i - a]
        if i >= b:
            prefix_sum[i] += prefix_sum[i - b]

    # Calculate the sum of all f(i) for i from 0 to m
    total_sum = 0
    for i in range(max_reachable + 1):
        if i <= m:
            total_sum += prefix_sum[i]

    return total_sum


