from collections import deque

def find_root(u, parent):
    if parent[u] != u:
        parent[u] = find_root(parent[u], parent)
    return parent[u]

def main():
    n, k = map(int, input().split())
    graph = [[] for _ in range(n + 1)]
    for _ in range(n - 1):
        u, v = map(int, input().split())
        graph[u].append(v)
        graph[v].append(u)

    parent = [i for i in range(n + 1)]
    size = [1] * (n + 1)

    queue = deque()
    for i in range(1, n + 1):
        if len(graph[i]) == 1:
            queue.append(i)

    while queue:
        u = queue.popleft()
        for v in graph[u]:
            if find_root(u, parent) != find_root(v, parent):
                parent[find_root(v, parent)] = find_root(u, parent)
                size[find_root(u, parent)] += size[find_root(v, parent)]
                queue.append(v)

    root_to_node = {}
    for i in range(1, n + 1):
        root = find_root(i, parent)
        if root not in root_to_node:
            root_to_node[root] = []
        root_to_node[root].append(i)

    max_size = 0
    max_root = 0
    for root, nodes in root_to_node.items():
        total_size = sum(size[node] for node in nodes)
        if total_size > max_size:
            max_size = total_size
            max_root = root

    result = []
    for i in range(1, n + 1):
        if find_root(i, parent) != max_root:
            result.append(i)

    print(*sorted(result)[:k])

