def solve(n, k, f):
    MOD = 998244353

    # Initialize dp array
    dp = [[0] * (k + 1) for _ in range(n + 1)]
    dp[0][0] = 1

    # Calculate dp values
    for i in range(1, n + 1):
        for c in range(k + 1):
            if c <= k:
                # For the current bit, we can either append 0 or 1
                dp[i][c] += dp[i - 1][c]
                dp[i][c] %= MOD
                if c > 0:
                    # If we append 1, the count of the current string increases by 1
                    dp[i][c] += dp[i - 1][c - 1]
                    dp[i][c] %= MOD

    # Calculate answer
    ans = 0
    for c in range(k + 1):
        if c >= f:
            ans += dp[n][c]

    return ans % MOD


