def solve():
    n, x = map(int, sys.stdin.readline().split())
    vouchers = []
    for _ in range(n):
        l, r, cost = map(int, sys.stdin.readline().split())
        vouchers.append((l, r, cost))

    min_total_cost = float('inf')

    for i in range(n):
        for j in range(i + 1, n):
            l1, r1, cost1 = vouchers[i]
            l2, r2, cost2 = vouchers[j]

            duration1 = r1 - l1 + 1
            duration2 = r2 - l2 + 1

            if duration1 + duration2 == x:
                # Check for disjoint condition: r1 < l2 or r2 < l1
                if r1 < l2 or r2 < l1:
                    min_total_cost = min(min_total_cost, cost1 + cost2)

    if min_total_cost == float('inf'):
        sys.stdout.write("-1\n")
    else:
        sys.stdout.write(str(min_total_cost) + "\n")

