    if data[p].strip() == "":
        p += 1
        continue
    try:
        n = int(data[p].strip())
    except:
        break
    p += 1
    if p + n - 1 >= len(data):
        break
    grid = []
    for i in range(n):
        line = data[p + i].rstrip()
        # If line is shorter/longer, take exactly n chars or pad/truncate
        if len(line) < n:
            line = line.ljust(n)
        else:
            line = line[:n]
        grid.append(line)
    p += n

    diag_char = grid[0][0]
    other_char = grid[0][1]
    ok = True
    if diag_char == other_char:
        ok = False
