        for i in range(n):
            for j in range(n):
                if j == i or j == n - 1 - i:
                    if grid[i][j] != diag_char:
                        ok = False
                        break
                else:
                    if grid[i][j] != other_char:
                        ok = False
                        break
            if not ok:
                break
    out_lines.append("YES" if ok else "NO")

if out_lines:
