import sys

def main():
    data = list(map(int, sys.stdin.read().strip().split()))
    if not data:
        return
    it = iter(data)
    n = next(it)
    x = next(it)
    intervals = []
    max_coord = 0
    for _ in range(n):
        l = next(it); r = next(it); c = next(it)
        intervals.append((l, r, c, r - l + 1))
        if r > max_coord:
            max_coord = r
    # sort intervals by start for sweeping, and by end for adding
    intervals_by_start = sorted(intervals, key=lambda v: v[0])
    intervals_by_end = sorted(intervals, key=lambda v: v[1])
    INF = 10**30
    max_len = max_coord if max_coord > x else x
    # durations can be up to 2e5; use size x (we only need durations up to x)
    min_cost_for_len = [INF] * (x + 1)
    ans = INF
    j = 0  # pointer for intervals_by_end
    m = len(intervals_by_end)
    for l, r, c, length in intervals_by_start:
        # add all intervals that end before current start
        while j < m and intervals_by_end[j][1] < l:
            el, er, ec, elen = intervals_by_end[j]
            if elen <= x:
                if ec < min_cost_for_len[elen]:
                    min_cost_for_len[elen] = ec
            j += 1
        need = x - length
        if 1 <= need <= x:
            if min_cost_for_len[need] < INF:
                total = c + min_cost_for_len[need]
                if total < ans:
