import sys
import ast

def parse_input_all(s):
    s = s.strip()
    if not s:
        return []
    # If it's a Python list literal, parse it
    if s[0] == '[':
        try:
            lst = ast.literal_eval(s)
            if isinstance(lst, list) and all(isinstance(x, str) for x in lst):
                return lst
        except Exception:
            pass
    # Otherwise, split by newlines, ignore empty lines
    parts = [line.strip() for line in s.splitlines() if line.strip() != ""]
    # If single line and not a list, treat the whole line as one name
    if len(parts) == 1:
        return [parts[0]]
    return parts

def extract_parts(name):
    # find jaws between '?(' and '):-'
    a = name.find('?(')
    b = name.find('):-', a+2)
    if a == -1 or b == -1:
        # fallback: try '?(' and '):'
        b = name.find('):', a+2)
        if a == -1 or b == -1:
            return None, None
    jaws = name[a+2:b]
    # stomach from after '):-' to last '.'
    start = b + 3
    end = name.rfind('.')
    if end == -1 or end < start:
        stomach = name[start:].strip()
    else:
        stomach = name[start:end].strip()
    return jaws, stomach

def parse_jaws(jaws):
    slots = []
    i = 0
    maxlen = 0
    n = len(jaws)
    while i < n:
        if jaws[i] == '_':
            j = i
            while j < n and jaws[j] == '_':
                j += 1
            l = j - i
            slots.append(l)
            if l > maxlen:
                maxlen = l
            i = j
        else:
            i += 1
    return slots, maxlen

def parse_stomach(stomach):
    # split by commas
    if not stomach:
        return []
    parts = [p.strip() for p in stomach.split(',') if p.strip() != ""]
    cons = []
    for p in parts:
        # find first '<' or '>'
        op_pos = -1
        op = None
        for idx,ch in enumerate(p):
            if ch == '<' or ch == '>':
                op_pos = idx
                op = ch
                break
        if op_pos == -1:
            continue
        # count underscores contiguous left of op
        lcount = 0
        i = op_pos - 1
        while i >= 0 and p[i] == '_':
            lcount += 1
            i -= 1
        # count underscores contiguous right of op
        rcount = 0
        i = op_pos + 1
        while i < len(p) and p[i] == '_':
            rcount += 1
            i += 1
        if lcount == 0 or rcount == 0:
            continue
        cons.append((lcount, op, rcount))
    return cons

def feasible(M, constraints, assigned):
    # nodes 0..M, where 0 is source representing 0
    N = M + 1
    edges = []
    # domain 0 <= xi <= 9 => xi - 0 <= 9  (edge 0 -> i weight 9)
    # and 0 - xi <= 0 => xi >= 0 => edge i -> 0 weight 0
    for i in range(1, M+1):
        edges.append((0, i, 9))
        edges.append((i, 0, 0))
    # stomach constraints: for a < b => a - b <= -1 => edge b -> a weight -1
    # for a > b => b - a <= -1 => edge a -> b weight -1
    for a, op, b in constraints:
        if a < 1 or a > M or b < 1 or b > M:
            # referencing variable outside jaws -> impossible
            return False
        if op == '<':
            edges.append((b, a, -1))
        else:
            edges.append((a, b, -1))
    # assignments: xi = val => xi - 0 <= val (0->i weight val) and 0 - xi <= -val (i->0 weight -val)
    for i, val in assigned.items():
        if val is None:
            continue
        if not (0 <= val <= 9):
            return False
        edges.append((0, i, val))
        edges.append((i, 0, -val))
    # Bellman-Ford: detect negative cycle. Initialize dist = 0 for all nodes
    dist = [0] * (N)
    # Relax N times; if relax on N-th iteration then negative cycle
    for _ in range(N):
        updated = False
        for (u, v, w) in edges:
            if dist[v] > dist[u] + w:
                dist[v] = dist[u] + w
                updated = True
        if not updated:
            return True
    return False

def solve_name(name):
    jaws, stomach = extract_parts(name)
    if jaws is None:
        return "false"
    slots, M = parse_jaws(jaws)
    # If no slots, no variables; trivial
    if len(slots) == 0:
        return ""
    constraints = parse_stomach(stomach)
    # Quick check: all indices in constraints must be between 1 and M
    for a, op, b in constraints:
        if a < 1 or a > M or b < 1 or b > M:
            return "false"
    assigned = dict()  # index -> val
    # we'll build result sequence of digits for each slot
    res = []
    # iterate slots in order
    for idx in slots:
        if idx in assigned and assigned[idx] is not None:
            res.append(str(assigned[idx]))
            continue
        # need to pick smallest digit for variable idx
        chosen = None
        for d in range(0, 10):
            assigned[idx] = d
            if feasible(M, constraints, assigned):
                chosen = d
                break
            # else undo and try next
            assigned[idx] = None
        if chosen is None:
            return "false"
        assigned[idx] = chosen
        res.append(str(chosen))
    return "".join(res)

def main():
    data = sys.stdin.read()
    names = parse_input_all(data)
    if not names:
        return
    results = []
    for name in names:
        results.append(solve_name(name))
    # Output: if input was a single name, print single result; if multiple, mimic Python list format
    if len(names) == 1:
        print(results[0])
    else:
        # format like sample: ['a', 'b', ...]
        out = "[" + ", ".join("'" + r + "'" for r in results) + "]"
        print(out)

if __name__ == "__main__":
    main()

