import sys

def solve_all(tokens):
    idx = 0
    out_lines = []
    while idx < len(tokens):
        if idx + 1 >= len(tokens):
            break
        n = int(tokens[idx]); l = int(tokens[idx+1]); idx += 2
        if idx + n > len(tokens):
            break
        a = list(map(int, tokens[idx:idx+n])); idx += n
        a.sort()
        max_gap = 0
        for i in range(1, n):
            gap = a[i] - a[i-1]
            if gap > max_gap:
                max_gap = gap
        # required radius is max of (first lantern to 0), (l - last lantern), and half of max internal gap
        req = max(a[0], l - a[-1], max_gap / 2.0)
        out_lines.append("{:.10f}".format(req))
    return out_lines

def main():
    data = sys.stdin.read().strip().split()
    if not data:
        return
    results = solve_all(data)
    sys.stdout.write("\n".join(results))

if __name__ == "__main__":
    main()

