import sys
from math import gcd

def main():
    data = sys.stdin.read().split()
    if not data:
        return
    n = int(data[0])
    A = data[1].strip()
    B = data[2].strip()
    m = len(A)
    k = len(B)
    def lcm(x,y):
        return x // gcd(x,y) * y
    L = lcm(m,k)
    def outcome(a,b):
        if a == b:
            return 0
        if (a == 'R' and b == 'S') or (a == 'S' and b == 'P') or (a == 'P' and b == 'R'):
            return 1  # A wins -> Polycarpus loses
        else:
            return -1 # B wins -> Nikephoros loses
    winA_period = 0  # times A wins -> Polycarpus losses
    winB_period = 0  # times B wins -> Nikephoros losses
    for i in range(L):
        res = outcome(A[i % m], B[i % k])
        if res == 1:
            winA_period += 1
        elif res == -1:
            winB_period += 1
    full = n // L
    rem = n % L
    totalA = winA_period * full
    totalB = winB_period * full
    for i in range(rem):
        res = outcome(A[i % m], B[i % k])
        if res == 1:
            totalA += 1
        elif res == -1:
            totalB += 1
    # Output: Nikephoros losses (totalB) then Polycarpus losses (totalA)
    print(totalB, totalA)

if __name__ == "__main__":
    main()

import sys

from collections import deque, defaultdict

def main():
    data = sys.stdin.read().strip().split()
    if not data:
        return
    ptr = 0
    n = int(data[ptr]); ptr += 1
    l = data[ptr]; ptr += 1
    r = data[ptr]; ptr += 1

    left_pos = defaultdict(deque)
    right_pos = defaultdict(deque)
    left_q = deque()
    right_q = deque()

    for i, ch in enumerate(l, start=1):
        if ch == '?':
            left_q.append(i)
        else:
            left_pos[ch].append(i)
    for i, ch in enumerate(r, start=1):
        if ch == '?':
            right_q.append(i)
        else:
            right_pos[ch].append(i)

    res = []

    # match same letters
    for c in map(chr, range(ord('a'), ord('z')+1)):
        L = left_pos[c]
        R = right_pos[c]
        while L and R:
            res.append((L.popleft(), R.popleft()))

    # match left specific to right '?'
    for c in map(chr, range(ord('a'), ord('z')+1)):
        L = left_pos[c]
        while L and right_q:
            res.append((L.popleft(), right_q.popleft()))

    # match left '?' to right specific
    for c in map(chr, range(ord('a'), ord('z')+1)):
        R = right_pos[c]
        while R and left_q:
            res.append((left_q.popleft(), R.popleft()))

    # match remaining '?' to '?'
    while left_q and right_q:
