def main():
    data = sys.stdin.read().splitlines()
    if not data:
        return
    idx = 0
    n = int(data[idx].strip()); idx += 1
    l = data[idx].strip(); idx += 1
    r = data[idx].strip(); idx += 1

    left_pos = {}
    right_pos = {}
    left_q = []
    right_q = []

    for i, ch in enumerate(l, start=1):
        if ch == '?':
            left_q.append(i)
        else:
            left_pos.setdefault(ch, []).append(i)
    for i, ch in enumerate(r, start=1):
        if ch == '?':
            right_q.append(i)
        else:
            right_pos.setdefault(ch, []).append(i)

    pairs = []

    # match same letters: for each letter, pair min counts
    for ch, lp in left_pos.items():
        rp = right_pos.get(ch, [])
        use = min(len(lp), len(rp))
        for i in range(use):
            pairs.append((lp[i], rp[i]))
        # remove used from right_pos
        if use > 0:
            right_pos[ch] = rp[use:]
    # remove used from left_pos (not needed further)

    # match remaining left specific to right '?'
    # collect all remaining left specific positions
    rem_left_spec = []
    for ch, lp in left_pos.items():
        if lp:
            rem_left_spec.extend(lp)
    # pair with right_q
    ri = 0
    while ri < len(right_q) and rem_left_spec:
        a = rem_left_spec.pop()
        b = right_q[ri]
        pairs.append((a, b))
        ri += 1
    right_q = right_q[ri:]

    # match remaining right specific to left '?'
    rem_right_spec = []
    for ch, rp in right_pos.items():
        if rp:
            rem_right_spec.extend(rp)
    li = 0
    while li < len(left_q) and rem_right_spec:
        a = left_q[li]
        b = rem_right_spec.pop()
        pairs.append((a, b))
        li += 1
    left_q = left_q[li:]

    # pair remaining left_q with right_q
    i = 0
    while i < len(left_q) and i < len(right_q):
        pairs.append((left_q[i], right_q[i]))
        i += 1

    out = []
    out.append(str(len(pairs)))
    for a, b in pairs:
        out.append(f"{a} {b}")
    sys.stdout.write("\n".join(out))

