        remM = n - usedM
        # if someone has no remaining moves, must assign to other
        if remH == 0:
            res.append('M'); usedM += 1; continue
        if remM == 0:
            res.append('H'); usedH += 1; continue
        # weight if assigned now: 10^{rem-1}
        wH = pow10[remH-1]
        wM = pow10[remM-1]
        # choose larger weight
        if wH >= wM:
            res.append('H'); usedH += 1
        else:
            res.append('M'); usedM += 1
    print(''.join(res))

