import sys

def read_input():
    data = sys.stdin.read().strip().split()
    if not data: 
        return None
    return int(data[0])

def generate_snake_by_rows(N, start_left=True):
    A = [[0]*N for _ in range(N)]
    cur = 1
    for i in range(N):
        if (i%2==0) == start_left:
            for j in range(N):
                A[i][j] = cur; cur += 1
        else:
            for j in range(N-1, -1, -1):
                A[i][j] = cur; cur += 1
    return A

def transpose(A):
    N = len(A)
    B = [[0]*N for _ in range(N)]
    for i in range(N):
        for j in range(N):
            B[j][i] = A[i][j]
    return B

def reverse_rows(A):
    return [list(reversed(row)) for row in A]

def reverse_cols(A):
    return list(reversed(A))

def rotate90(A):
    N = len(A)
    B = [[0]*N for _ in range(N)]
    for i in range(N):
        for j in range(N):
            B[j][N-1-i] = A[i][j]
    return B

def simulate(A, piece):  # piece: 'rook' or 'queen'
    N = len(A)
    pos = {}
    for i in range(N):
        for j in range(N):
            pos[A[i][j]] = (i,j)
    visited = [[False]*N for _ in range(N)]
    total = N*N
    cur_num = 1
    ci,cj = pos[1]
    visited[ci][cj] = True
    visited_count = 1
    teleports = 0
    while visited_count < total:
        # find reachable unvisited cells from (ci,cj)
        best_num = None
        # iterate all cells; choose minimal number if reachable and unvisited
        for num in range(1, total+1):
            if num == 0: continue
            if num not in pos: continue
            i,j = pos[num]
            if visited[i][j]: continue
            reachable = False
            if piece == 'rook':
                if i == ci or j == cj:
                    reachable = True
            else:
                # queen
                if i == ci or j == cj or abs(i-ci) == abs(j-cj):
                    reachable = True
            if reachable:
                best_num = num
                break
        if best_num is None:
            # teleport to minimal unvisited
            teleports += 1
            for num in range(1, total+1):
                i,j = pos[num]
                if not visited[i][j]:
                    best_num = num
                    break
        # move to best_num
        ci,cj = pos[best_num]
        visited[ci][cj] = True
        visited_count += 1
    return teleports

def try_variants(N):
    variants = []
    # base snakes
    variants.append(generate_snake_by_rows(N, True))
    variants.append(generate_snake_by_rows(N, False))
    # transposed
    variants.append(transpose(variants[0]))
    variants.append(transpose(variants[1]))
    # reversed rows/cols
    variants.append(reverse_rows(variants[0]))
    variants.append(reverse_cols(variants[0]))
    # rotations
    variants.append(rotate90(variants[0]))
    variants.append(rotate90(rotate90(variants[0])))
    # unique them by string
    seen = set()
    uniq = []
    for B in variants:
        key = tuple(tuple(row) for row in B)
        if key not in seen:
            seen.add(key)
            uniq.append(B)
    return uniq

def main():
    N = read_input()
    if N is None:
        return
    # quick impossible cases guess
    if N <= 2:
        print(-1)
        return
    # Try variants of snake-like Hamiltonian paths and pick one where rook teleports < queen teleports
    candidates = try_variants(N)
    for A in candidates:
        r = simulate(A, 'rook')
        q = simulate(A, 'queen')
        if r < q:
            for row in A:
                print(' '.join(map(str,row)))
            return
    # If not found, attempt some randomized variants by permuting row directions and then rotating/flipping
    import random, time
    start_time = time.time()
    timeout = 1.8  # seconds
    while time.time() - start_time < timeout:
        # random snake-like: choose for each row direction but ensure adjacency by starting column matching previous end
        # We'll build path by rows: for row 0 choose left or right randomly, then for row i choose direction but ensure start column = end column of previous
        # To allow that, we enforce that each row start column equals previous end column by possibly shifting columns using rotation; to keep simple, we just randomly rotate/flip entire base.
        base = generate_snake_by_rows(N, random.choice([True, False]))
        # apply random transforms
        transforms = random.randint(0,7)
        B = base
        if transforms & 1: B = reverse_rows(B)
        if transforms & 2: B = reverse_cols(B)
        if transforms & 4: B = transpose(B)
        r = simulate(B, 'rook')
        q = simulate(B, 'queen')
        if r < q:
            for row in B:
                print(' '.join(map(str,row)))
            return
    # fallback: try a known working small pattern tiled when possible (try to tile 4x4 sample)
    if N >= 4:
        # tile using the sample 4x4 pattern, filling blocks left-to-right top-to-bottom with offsets
        base4 = [
            [4,3,6,12],
            [7,5,9,15],
            [14,1,11,10],
            [13,8,16,2]
        ]
        A = [[0]*N for _ in range(N)]
        cur_block = 0
        num = 1
        for bi in range(0, N, 4):
            for bj in range(0, N, 4):
                for i in range(4):
                    for j in range(4):
                        ii = bi+i
                        jj = bj+j
                        if ii < N and jj < N:
                            A[ii][jj] = num + base4[i][j] - 1
                num += 16
        # fill any remaining zeros sequentially
        nxt = 1
        used = set()
        for i in range(N):
            for j in range(N):
                if A[i][j] != 0:
                    used.add(A[i][j])
        for i in range(N):
            for j in range(N):
                if A[i][j] == 0:
                    while nxt in used:
                        nxt += 1
                    A[i][j] = nxt
                    used.add(nxt)
        r = simulate(A, 'rook')
        q = simulate(A, 'queen')
        if r < q:
            for row in A:
                print(' '.join(map(str,row)))
            return
    # Give up
    print(-1)

