import sys

def check(s: str) -> bool:
    if not s:
        return False
    # must consist only of a, b, c
    if any(ch not in 'abc' for ch in s):
        return False
    # find blocks: a+ b+ c+
    i = 0
    n = len(s)
    # count a
    cnt_a = 0
    while i < n and s[i] == 'a':
        cnt_a += 1
        i += 1
    # next must be b
    cnt_b = 0
    while i < n and s[i] == 'b':
        cnt_b += 1
        i += 1
    # next must be c
    cnt_c = 0
    while i < n and s[i] == 'c':
        cnt_c += 1
        i += 1
    # entire string must be consumed and counts of a,b,c all >=1
    if i != n:
        return False
    if cnt_a < 1 or cnt_b < 1 or cnt_c < 1:
        return False
    # c count must equal a or b
    return cnt_c == cnt_a or cnt_c == cnt_b

