import sys

def max_wins(n: int) -> int:
    # The maximum number of games the winner can play is floor(log2(n))
    # but if n is not a power of two, winner can play one extra game.
    # Equivalent: return floor(log2(n-1))+1 if n>1 else 0
    if n <= 1:
        return 0
    # check power of two
    if (n & (n - 1)) == 0:
        # n is power of two
        k = n.bit_length() - 1
        return k
    else:
        return n.bit_length()

