import sys, math

def solve_pair(a, b):
    if a == b:
        return 0
    if a > b:
        a, b = b, a
    d = b - a
    best_k = 0
    best_lcm = (a * b) // math.gcd(a, b)
    i = 1
    while i * i <= d:
        if d % i == 0:
            for g in (i, d // i):
                k = (-a) % g
                aa = a + k
                bb = b + k
                l = aa // math.gcd(aa, bb) * bb
                if l < best_lcm or (l == best_lcm and k < best_k):
                    best_lcm = l
                    best_k = k
        i += 1
    return best_k

