import sys, math

def best_k_for_pair(a, b):
    if a == b:
        return 0
    d = abs(a - b)
    best_k = 0
    best_lcm = 1 << 62
    r = int(math.isqrt(d))
    for i in range(1, r + 1):
        if d % i == 0:
            for g in (i, d // i):
                k = (g - (a % g)) % g
                na = a + k
                nb = b + k
                g2 = math.gcd(na, nb)
                lcm = (na // g2) * nb
                if lcm < best_lcm or (lcm == best_lcm and k < best_k):
                    best_lcm = lcm
                    best_k = k
    return best_k

