        (10, 'X'), (9, 'IX'), (5, 'V'), (4, 'IV'), (1, 'I')
    ]
    res = []
    num = n
    for v, sym in vals:
        if num == 0:
            break
        q = num // v
        if q:
            res.append(sym * q if len(sym) == 1 else sym * q if v < 10 else sym * q if v >= 1000 else sym * q)
            # The above handles repeats; for subtractive symbols q will be 0 or 1 in practice.
