import sys
from collections import defaultdict, deque

MAXA = 10**6

def spf_sieve(n):
    spf = list(range(n+1))
    for i in range(2, int(n**0.5)+1):
        if spf[i]==i:
            for j in range(i*i, n+1, i):
                if spf[j]==j:
                    spf[j]=i
    return spf

spf = spf_sieve(MAXA)

def factorize(x):
    res = {}
    while x>1:
        p = spf[x]
        c = 0
        while x%p==0:
            x//=p
            c+=1
        res[p]=c
    return res

