    g = int(next(it))
    l = int(next(it))
    edges.append((u,v,g,l))

# Quick check: for each edge l % g == 0
for (u,v,g,l) in edges:
    if l % g != 0:
        print("NO")
        sys.exit(0)

# Collect per-prime constraints
prime_constraints = defaultdict(list)  # p -> list of (u,v,eg,el)
for idx,(u,v,g,l) in enumerate(edges):
    fg = factorize(g)
    fl = factorize(l)
    primes = set(fg.keys()) | set(fl.keys())
    for p in primes:
        eg = fg.get(p,0)
        el = fl.get(p,0)
        if eg > el:
            print("NO"); sys.exit(0)
        prime_constraints[p].append((u,v,eg,el))

# exponent per prime per node
exp_per_prime = {}  # p -> list of exponents length n (default 0)
for p, plist in prime_constraints.items():
    adj = [[] for _ in range(n)]
    assigned = [-1]*n
    possible_vals = [set() for _ in range(n)]
    # build adjacency
    for (u,v,eg,el) in plist:
        adj[u].append((v,eg,el))
        adj[v].append((u,eg,el))
        possible_vals[u].add(eg); possible_vals[u].add(el)
        possible_vals[v].add(eg); possible_vals[v].add(el)
    # process edges with eg==el -> force assign both
    q = deque()
    for (u,v,eg,el) in plist:
        if eg==el:
            if assigned[u]!=-1 and assigned[u]!=eg:
                print("NO"); sys.exit(0)
            if assigned[v]!=-1 and assigned[v]!=eg:
                print("NO"); sys.exit(0)
            if assigned[u]==-1:
                assigned[u]=eg; q.append(u)
            if assigned[v]==-1:
                assigned[v]=eg; q.append(v)
    # BFS propagate forced
    while q:
        u = q.popleft()
        Eu = assigned[u]
        for (v,eg,el) in adj[u]:
            # if Eu equals eg then v must be el; if Eu equals el then v must be eg; else impossible
            if Eu==eg:
                need = el
            elif Eu==el:
                need = eg
            else:
                print("NO"); sys.exit(0)
            if assigned[v]==-1:
                assigned[v]=need; q.append(v)
            elif assigned[v]!=need:
                print("NO"); sys.exit(0)
    # For remaining components unassigned, try choices
    vis = [False]*n
    for i in range(n):
        if assigned[i]!=-1 or not adj[i]:
            continue
        if vis[i]:
            continue
        # gather component nodes via stack
        comp = []
        stack = [i]; vis[i]=True
        while stack:
            u = stack.pop()
            comp.append(u)
            for v,eg,el in adj[u]:
                if not vis[v]:
                    vis[v]=True; stack.append(v)
        # try to assign this component by picking a start node and trying each possible value from possible_vals[start]
        success = False
        start = comp[0]
        start_choices = list(possible_vals[start])
        for choice in start_choices:
            # try assign with rollback
            assigned_local = []
            good = True
            assign_stack = [start]
            assigned[start]=choice
            assigned_local.append(start)
            while assign_stack and good:
                u = assign_stack.pop()
                Eu = assigned[u]
                for (v,eg,el) in adj[u]:
                    if Eu==eg:
                        need = el
                    elif Eu==el:
                        need = eg
                    else:
                        good = False; break
                    if assigned[v]==-1:
                        assigned[v]=need
                        assigned_local.append(v)
                        assign_stack.append(v)
                    elif assigned[v]!=need:
                        good = False; break
            if good:
                success = True
                break
            else:
                # rollback assigned in this trial
                for node in assigned_local:
                    assigned[node] = -1
        if not success:
            print("NO"); sys.exit(0)
    # remaining nodes with no incident edges keep exponent 0
    for i in range(n):
        if assigned[i]==-1:
            assigned[i]=0
    exp_per_prime[p]=assigned

# Construct final numbers
ans = [1]*n
for p, arr in exp_per_prime.items():
