        if node in visited:
            continue
        # BFS to set s[node], off[node] so E[node] = s*X + off
        queue = deque([node])
        s = {node:1}
        off = {node:0}
        visited_local = set([node])
        x_fixed = None
        possible = True
        while queue and possible:
            u = queue.popleft()
            for v,C in adj[u]:
                # E[v] = C - E[u] = -s[u]*X + (C - off[u])
                sv = -s[u]
                offv = C - off[u]
                if v not in s:
                    s[v]=sv; off[v]=offv
                    visited_local.add(v)
                    queue.append(v)
                else:
                    # check consistency: s[v]*X + off[v] == sv*X + offv
                    if s[v]==sv:
                        if off[v] != offv:
                            possible = False
                            break
                    else:
                        # s[v] == -sv. So 2*s[v]*X = offv - off[v]
                        denom = 2*s[v]
                        num = offv - off[v]
                        # X must be integer: num divisible by denom
                        if denom == 0:
                            if num != 0:
                                possible = False
                                break
                        else:
                            if num % denom != 0:
                                possible = False
                                break
                            val = num//denom
                            if x_fixed is None:
                                x_fixed = val
                            else:
                                if x_fixed != val:
                                    possible = False
                                    break
            # end for neighbors
        if not possible:
            print("NO")
