            for u in comp_nodes:
                if s[u]==1:
                    # x >= -off[u]
                    lo = max(lo, -off[u])
                else:
                    # -x + off[u] >=0 -> x <= off[u]
                    hi = min(hi, off[u])
            if lo > hi:
                print("NO")
                sys.exit(0)
            x_fixed = lo  # choose minimal to keep numbers small
        # compute exponents and check nonnegative
        for u in comp_nodes:
            E = s[u]*x_fixed + off[u]
            if E < 0:
                print("NO")
                sys.exit(0)
            exponents[u][p] = E
        # mark visited
        for u in comp_nodes:
            visited[u]=True

