import sys
from collections import defaultdict, deque

def sieve_primes(n):
    sieve = [True] * (n+1)
    primes = []
    for i in range(2, n+1):
        if sieve[i]:
            primes.append(i)
            step = i
            for j in range(i*i, n+1, step):
                sieve[j] = False
    return primes

_PRIMES = sieve_primes(10**6)

def factorize(x):
    res = {}
    for p in _PRIMES:
        if p*p > x:
            break
        if x % p == 0:
            cnt = 0
            while x % p == 0:
                x //= p
                cnt += 1
            res[p] = cnt
        if x == 1:
            break
    if x > 1:
        res[x] = res.get(x, 0) + 1
    return res

def main():
    data = sys.stdin.read().strip().split()
    if not data:
        return
    it = iter(data)
    n = int(next(it))
    m = int(next(it))
    edges = []
    for _ in range(m):
        u = int(next(it)) - 1
        v = int(next(it)) - 1
        g = int(next(it))
        l = int(next(it))
        edges.append((u,v,g,l))
    # basic check
    for (_,_,g,l) in edges:
        if l % g != 0:
            print("NO")
            return
    # build prime -> list of (u,v,gp,lp)
    prime_edges = defaultdict(list)
    # also store for verification final
    for idx,(u,v,g,l) in enumerate(edges):
        fg = factorize(g)
        fl = factorize(l)
        primes = set(fg.keys()) | set(fl.keys())
        for p in primes:
            gp = fg.get(p, 0)
            lp = fl.get(p, 0)
            if lp < gp:
                print("NO")
                return
            prime_edges[p].append((u,v,gp,lp))
    # store exponents per prime per node
    exponents = defaultdict(lambda: defaultdict(int))  # exponents[p][v] = exp
    # Process each prime
    for p, pelist in prime_edges.items():
        # nodes involved
        nodes = set()
        for (u,v,gp,lp) in pelist:
            nodes.add(u); nodes.add(v)
        nodes = list(nodes)
        node_index = {v:i for i,v in enumerate(nodes)}
        N = len(nodes)
        # domains
        D = [set() for _ in range(N)]
        # adjacency and allowed pairs per edge
        adj = [[] for _ in range(N)]
        edge_allowed = {}
        for (u,v,gp,lp) in pelist:
            iu = node_index[u]; iv = node_index[v]
            if gp == lp:
                pairs = {(gp,gp)}
            else:
                pairs = {(gp,lp),(lp,gp)}
            adj[iu].append(iv)
            adj[iv].append(iu)
            edge_allowed[(iu,iv)] = pairs
            edge_allowed[(iv,iu)] = set((b,a) for (a,b) in pairs)
            # initial domain union of gp and lp
            D[iu].add(gp); D[iu].add(lp)
            D[iv].add(gp); D[iv].add(lp)
        # AC-3
        q = deque()
        for (u,v,_,_) in pelist:
            iu = node_index[u]; iv = node_index[v]
            q.append((iu,iv))
            q.append((iv,iu))
        def revise(iu, iv):
            removed = False
            allowed = edge_allowed.get((iu,iv), set())
            if not allowed:
                return False
            toremove = []
            for a in D[iu]:
                ok = False
                for b in D[iv]:
                    if (a,b) in allowed:
                        ok = True; break
                if not ok:
                    toremove.append(a)
            if toremove:
                for a in toremove:
                    D[iu].remove(a)
                removed = True
            return removed
        while q:
            iu, iv = q.popleft()
            if revise(iu, iv):
                if not D[iu]:
                    print("NO"); return
                for k in adj[iu]:
                    if k != iv:
                        q.append((k, iu))
        # Now backtrack per connected component to assign values
        comp_vis = [False]*N
        assign = [None]*N
        for i in range(N):
            if comp_vis[i]:
                continue
            # gather component nodes
            stack = [i]; comp = []
            comp_vis[i] = True
            while stack:
                x = stack.pop()
                comp.append(x)
                for y in adj[x]:
                    if not comp_vis[y]:
                        comp_vis[y]=True
                        stack.append(y)
            # backtracking
            comp_sorted = sorted(comp, key=lambda x: len(D[x]))
            L = len(comp_sorted)
            success = False
            def dfs(pos):
                nonlocal success
                if success:
                    return
                if pos==L:
                    success = True
                    return
                v = comp_sorted[pos]
                for val in list(D[v]):
                    ok = True
                    # check neighbors already assigned
                    for nb in adj[v]:
                        if assign[nb] is not None:
                            if (val, assign[nb]) not in edge_allowed.get((v,nb), set()):
                                ok = False; break
                    if not ok:
                        continue
                    assign[v]=val
                    dfs(pos+1)
                    if success:
                        return
                    assign[v]=None
            dfs(0)
            if not success:
                print("NO"); return
        # write assigned exponents
        for idx,node in enumerate(nodes):
            exp = assign[idx]
            if exp is None:
                # node isolated? shouldn't happen
                exp = next(iter(D[idx]))
            exponents[p][node] = exp
    # build final a_v
    a = [1]*n
    for p, mp in exponents.items():
        for v, e in mp.items():
            if e>0:
                a[v] *= pow(p, e)
    # verify edges match gcd and lcm
    import math
    for (u,v,g,l) in edges:
        guv = math.gcd(a[u], a[v])
        luv = a[u]//guv*a[v]
        if guv != g or luv != l:
            print("NO")
            return
    # For nodes with no primes at all, a[v] stays 1
    print("YES")
    print(" ".join(str(x) for x in a))

if __name__ == "__main__":
    main()

