        # build adjacency
        adj = defaultdict(list)
        nodes_set = set()
        for (u,v,gp,lp) in elist:
            adj[u].append((v,gp,lp))
            adj[v].append((u,gp,lp))
            nodes_set.add(u); nodes_set.add(v)
        allowed = {}
        for node in nodes_set:
            allowed[node] = None
        for (u,v,gp,lp) in elist:
            set_uv = {gp, lp}
            # intersect for u
            if allowed[u] is None:
                allowed[u] = set_uv.copy()
            else:
                allowed[u] &= set_uv
            if not allowed[u]:
                print("NO"); return
            # for v
            if allowed[v] is None:
                allowed[v] = set_uv.copy()
            else:
                allowed[v] &= set_uv
            if not allowed[v]:
                print("NO"); return
        # assign map
        assigned = {}
        q = deque()
        # helper assign function
        def do_assign(start_node, start_val):
            # returns (success, list_of_assigned_nodes_in_this_run)
            local_q = deque()
            changed = []
            assigned[start_node] = start_val
            local_q.append(start_node)
            changed.append(start_node)
            while local_q:
                u = local_q.popleft()
                val_u = assigned[u]
                for (v,gp,lp) in adj[u]:
                    # required for v
                    if val_u == gp:
                        need = lp
                    elif val_u == lp:
                        need = gp
                    else:
                        # u's value not in this edge's pair => impossible
                        # but this should not happen if allowed was intersection
                        return False, changed
                    if v in assigned:
                        if assigned[v] != need:
                            return False, changed
                    else:
                        if need not in allowed[v]:
                            return False, changed
                        assigned[v] = need
                        local_q.append(v)
                        changed.append(v)
            return True, changed
        # first process nodes with allowed size 1
        singleton_nodes = [node for node, s in allowed.items() if s is not None and len(s)==1]
        for node in singleton_nodes:
            if node in assigned: continue
            val = next(iter(allowed[node]))
