        # now remaining unassigned nodes in this prime's nodes_set
        for node in list(nodes_set):
            if node in assigned: continue
            # node allowed must be non-empty set with size >=1
            opts = allowed[node]
            if opts is None:
                # shouldn't happen for nodes_set
                opts = {0}
            success = False
            opts_list = list(opts)
