                ei = 0
            if ei:
                node_vals[i] *= pow(p, ei)
    # Validate all edges
    import math
    for (u,v,g,l) in edges:
        au = node_vals[u]
        av = node_vals[v]
        gg = math.gcd(au, av)
        ll = (au // gg) * av
        if gg != g or ll != l:
            print("NO")
            return
    print("YES")
    print(" ".join(str(x) for x in node_vals))

