import sys

MOD = 998244353

def modpow(a, e):
    res = 1
    a %= MOD
    while e > 0:
        if e & 1:
            res = res * a % MOD
        a = a * a % MOD
        e >>= 1
    return res

def prepare_fact(n):
    fact = [1] * (n+1)
    invfact = [1] * (n+1)
    for i in range(1, n+1):
        fact[i] = fact[i-1] * i % MOD
    invfact[n] = modpow(fact[n], MOD-2)
    for i in range(n, 0, -1):
        invfact[i-1] = invfact[i] * i % MOD
    return fact, invfact

def comb(n, k, fact, invfact):
    if k < 0 or k > n: return 0
    return fact[n] * invfact[k] % MOD * invfact[n-k] % MOD

def main():
    data = sys.stdin.read().strip().split()
    if not data:
        return
    n = int(data[0])
    if n % 2 == 1:
        print(0)
        return
    if n == 0:
        print(0)
        return
    fact, invfact = prepare_fact(n)
    ans = 0
    # sum k=1..n-1 (-1)^k * C(n-2,k-1) * k^{k-1} * (n-k)^{n-k-1}
    for k in range(1, n):
        c = comb(n-2, k-1, fact, invfact)
        a = 1
        if k >= 1:
            a = modpow(k, k-1)
        b = 1
        nk = n-k
        if nk >= 1:
            b = modpow(nk, nk-1)
        term = c * a % MOD * b % MOD
        if k % 2 == 1:
            ans = (ans - term) % MOD
        else:
            ans = (ans + term) % MOD
    print(ans % MOD)

