import sys
MOD = 998244353

def modpow(a, e):
    if e < 0:
        return 1
    return pow(a, e, MOD)

def prepare_fact(n):
    fact = [1] * (n+1)
    ifact = [1] * (n+1)
    for i in range(1, n+1):
        fact[i] = fact[i-1] * i % MOD
    ifact[n] = pow(fact[n], MOD-2, MOD)
    for i in range(n, 0, -1):
        ifact[i-1] = ifact[i] * i % MOD
    return fact, ifact

def nCr(n, r, fact, ifact):
    if r < 0 or r > n:
        return 0
    return fact[n] * ifact[r] % MOD * ifact[n-r] % MOD

data = sys.stdin.read().strip().split()
out = []
for token in data:
    n = int(token)
    if n % 2 == 1:
        out.append("0")
        continue
    if n == 1:
        out.append("0")
        continue
    fact, ifact = prepare_fact(n)
    total = 0
    # sum over s = size of side containing n (1..n-1), with 1 on other side
    for s in range(1, n):
        comb = nCr(n-2, s-1, fact, ifact)
        a = s
        b = n - s
        if a == 1:
            ta = 1
        else:
            ta = pow(a, a-2, MOD)
        if b == 1:
            tb = 1
        else:
            tb = pow(b, b-2, MOD)
        ways = comb * ta % MOD * tb % MOD
        ways = ways * (a % MOD) % MOD * (b % MOD) % MOD
        sign = 1 - 2 * (s & 1)
        total = (total + ways * (sign % MOD)) % MOD
    out.append(str(total % MOD))
print("\n".join(out))

