def min_ternary_string(s: str) -> str:
    ones = s.count('1')
    t = [c for c in s if c != '1']  # only '0' and '2' in original order
    res = []
    inserted = False
    for c in t:
        if c == '2' and not inserted:
            # insert all ones before the first '2'
            res.append('1' * ones)
            inserted = True
        res.append(c)
    if not inserted:
        # no '2' found, append all ones at the end
        res.append('1' * ones)
    return ''.join(res)

