        adjmat = [[False]*N for _ in range(N)]
        for (u,v) in edgeset:
            adjmat[u][v] = adjmat[v][u] = True
        def has_odd_cycle_after(adds):
            g = [[] for _ in range(N)]
            for u in range(N):
                for v in range(N):
                    if adjmat[u][v] or (u,v) in adds or (v,u) in adds:
                        if u < v:
