import math

def main():
    n = int(input())
    A = input().strip()
    B = input().strip()

    m = len(A)
    k = len(B)

    def lcm(a, b):
        return a * b // math.gcd(a, b)

    T = lcm(m, k)

    total_a_loss = 0
    total_b_loss = 0

    for i in range(T):
        a = A[i % m]
        b = B[i % k]
        if a == b:
            continue
        if (a == 'R' and b == 'S') or (a == 'S' and b == 'P') or (a == 'P' and b == 'R'):
            total_b_loss += 1
        else:
            total_a_loss += 1

    full_cycles = n // T
    remainder = n % T

    total_a = full_cycles * total_a_loss
    total_b = full_cycles * total_b_loss

    for i in range(remainder):
        a = A[i % m]
        b = B[i % k]
        if a == b:
            continue
        if (a == 'R' and b == 'S') or (a == 'S' and b == 'P') or (a == 'P' and b == 'R'):
            total_b += 1
        else:
            total_a += 1

    print(total_a, total_b)

if __name__ == "__main__":
    main()

import sys
from collections import defaultdict, deque

def main():
    input = sys.stdin.read().split()
    idx = 0
    n = int(input[idx])
    idx += 1
    l = input[idx]
    idx += 1
    r = input[idx]
    idx += 1

    # For each color, track the positions of left and right boots
    left_color_pos = defaultdict(list)
    right_color_pos = defaultdict(list)

    for i in range(n):
        c = l[i]
        if c != '?':
            left_color_pos[c].append(i)
        else:
            left_color_pos['?'].append(i)

    for i in range(n):
        c = r[i]
        if c != '?':
            right_color_pos[c].append(i)
        else:
            right_color_pos['?'].append(i)

    # Step 1: Pair specific colors
    used_left = [False] * n
    used_right = [False] * n
    pairs = []

    # For each color except '?'
    for c in left_color_pos:
        if c == '?':
            continue
        if c not in right_color_pos:
            continue
        left_list = left_color_pos[c]
        right_list = right_color_pos[c]
        min_len = min(len(left_list), len(right_list))
        for i in range(min_len):
            l_idx = left_list[i]
            r_idx = right_list[i]
            pairs.append((l_idx + 1, r_idx + 1))
            used_left[l_idx] = True
            used_right[r_idx] = True

    # Now collect remaining left and right boots
    remaining_left = []
    remaining_right = []
    for i in range(n):
        if not used_left[i]:
            remaining_left.append(i)
        if not used_right[i]:
            remaining_right.append(i)

    # Step 2: Pair wildcards
    # First, pair left '?' with right non-?
    # Then pair remaining left '?' with right '?'
    # Also, pair right '?' with left non-?

    # Create lists for left and right wildcards
    left_wild = []
    right_wild = []
    for i in remaining_left:
        if l[i] == '?':
            left_wild.append(i)
    for i in remaining_right:
        if r[i] == '?':
            right_wild.append(i)

    # Pair left wild with right non-wild
    for i in left_wild:
        for j in range(len(remaining_right)):
            if not used_right[remaining_right[j]]:
                pairs.append((i + 1, remaining_right[j] + 1))
                used_left[i] = True
                used_right[remaining_right[j]] = True
                break

    # Now pair remaining right wild with left non-wild
    for j in right_wild:
        for i in range(len(remaining_left)):
            if not used_left[remaining_left[i]]:
                pairs.append((remaining_left[i] + 1, j + 1))
                used_left[remaining_left[i]] = True
                used_right[j] = True
                break

    # Finally, pair remaining wild with wild
    left_wild_remaining = []
    right_wild_remaining = []
    for i in range(n):
        if not used_left[i] and l[i] == '?':
            left_wild_remaining.append(i)
        if not used_right[i] and r[i] == '?':
            right_wild_remaining.append(i)

    min_len = min(len(left_wild_remaining), len(right_wild_remaining))
    for i in range(min_len):
        pairs.append((left_wild_remaining[i] + 1, right_wild_remaining[i] + 1))
        used_left[left_wild_remaining[i]] = True
        used_right[right_wild_remaining[i]] = True

    print(len(pairs))
    for p in pairs:
        print(p[0], p[1])

