import sys
from collections import defaultdict

def main():
    input = sys.stdin.read
    data = input().split('\n')

    n = int(data[0])
    l_str = data[1]
    r_str = data[2]

    # Count specific colors (excluding '?') in left and right
    left_color_count = defaultdict(int)
    right_color_count = defaultdict(int)

    left_color_indices = defaultdict(list)
    right_color_indices = defaultdict(list)

    for i in range(n):
        lc = l_str[i]
        if lc != '?':
            left_color_count[lc] += 1
            left_color_indices[lc].append(i + 1)  # 1-based index
        else:
            left_color_indices['?'].append(i + 1)

        rc = r_str[i]
        if rc != '?':
            right_color_count[rc] += 1
            right_color_indices[rc].append(i + 1)
        else:
            right_color_indices['?'].append(i + 1)

    pairs = []

    # Step 1: Pair same specific colors
    for color in left_color_count:
        if color in right_color_count:
            count = min(left_color_count[color], right_color_count[color])
            l_list = left_color_indices[color]
            r_list = right_color_indices[color]
            for i in range(count):
                pairs.append((l_list[i], r_list[i]))

    # Step 2: Pair left '?' with right specific colors
    left_q = left_color_indices['?']
    for color in right_color_count:
        if color != '?':
            r_list = right_color_indices[color]
            for idx in range(len(r_list)):
                if left_q:
                    pairs.append((left_q.pop(), r_list[idx]))

    # Step 3: Pair right '?' with left specific colors
    right_q = right_color_indices['?']
    for color in left_color_count:
        if color != '?':
            l_list = left_color_indices[color]
            for idx in range(len(l_list)):
                if right_q:
                    pairs.append((l_list[idx], right_q.pop()))

    # Step 4: Pair remaining '?' with '?'
    while left_q and right_q:
        pairs.append((left_q.pop(), right_q.pop()))

    print(len(pairs))
