def compute_sum(k, m, n):
    total = 0
    current = 1
    for _ in range(m):
        total += current
        if total > n:
            return n + 1
        current *= k
    return total

def is_possible(n):
    if n == 1:
        return False
    max_m = 60  # since 2^60 is about 1e18
    for m in range(3, max_m + 1):
        # Check if the minimal sum for k=2 is <= n
        min_sum = (2 ** m) - 1
        if min_sum > n:
            break
        # Binary search for k in [2, ... upper]
        low = 2
        high = n
        while low <= high:
            mid = (low + high) // 2
            s = compute_sum(mid, m, n)
            if s == n:
                return True
            elif s < n:
                low = mid + 1
            else:
                high = mid - 1
    return False

