def main():
    import sys
    input = sys.stdin.read
    data = input().split()

    idx = 0
    n = int(data[idx])
    idx += 1
    m = int(data[idx])
    idx += 1

    weight_cost = []
    for _ in range(n):
        w = int(data[idx])
        idx += 1
        c = int(data[idx])
        idx += 1
        weight_cost.append((w, c))

    # Group items by weight
    weight_groups = {1: [], 2: [], 3: []}
    for w, c in weight_cost:
        weight_groups[w].append(c)

    # Sort each group in descending order of cost
    for w in weight_groups:
        weight_groups[w].sort(reverse=True)

    # Initialize dp array
    dp = [0] * (m + 1)

    # Process items of weight 1
    for i in range(1, m + 1):
        if i >= 1 and weight_groups[1]:
            dp[i] = dp[i - 1] + weight_groups[1][0]

    # Process items of weight 2
    for i in range(2, m + 1):
        if i >= 2 and weight_groups[2]:
            dp[i] = max(dp[i], dp[i - 2] + weight_groups[2][0])

    # Process items of weight 3
    for i in range(3, m + 1):
        if i >= 3 and weight_groups[3]:
            dp[i] = max(dp[i], dp[i - 3] + weight_groups[3][0])

    # Now use a greedy approach for each weight group to fill the rest
    for w in [1, 2, 3]:
        items = weight_groups[w]
        if not items:
            continue
        # Use as many of the best items as possible
        for i in range(w, m + 1):
            if i - w >= 0:
                dp[i] = max(dp[i], dp[i - w] + items[0])

    print(dp[m])

