    var_part, op_part = parts[0], parts[1]

    # Extract number of variables from underscores in op_part
    num_vars = op_part.count('_')
    if num_vars == 0:
        return None, None

    # Extract the operations and their positions
    ops = []
    for c in op_part:
        if c in '+-*/><':
            ops.append(c)

    return num_vars, ops

