    stack = []
    for op in ops:
        if op in '+-*/':
            if len(stack) < 2:
                return False
            a = stack.pop()
            b = stack.pop()
            if op == '+':
                res = a + b
            elif op == '-':
                res = b - a
            elif op == '*':
                res = a * b
            elif op == '/':
                if a == 0:
                    return False
                res = b // a
            stack.append(res)
        elif op == '>':
            if len(stack) < 1:
                return False
            stack.append(stack[-1] + 1)
        elif op == '<':
            if len(stack) < 1:
                return False
            stack.append(stack[-1] - 1)
        else:
            # Should not happen as per parsing
            return False
    return True

